buildscript {    repositories {        google()        mavenCentral()    }    dependencies {        classpath("com.android.tools.build:gradle:${Version.gradle}")        classpath("org.jetbrains.kotlin:kotlin-gradle-plugin:${Version.kotlin}")    }}tasks.register("clean", Delete::class) {    delete(rootProject.buildDir)}subprojects {    project.configurations.all {        resolutionStrategy.eachDependency {            val group = requested.group            val name = requested.name            val version = requested.version            println("依赖库：$group:$name:$version")        }    }}